import React, { useEffect, useState } from 'react'
import './styles/AddNewForm.css'
import 'bootstrap/dist/css/bootstrap.min.css'
import { useNavigate } from 'react-router-dom'
import ViewContractDetails from './ViewContractDetails'
import Modal from "react-modal";
import SendContract from './SendContract'


const ContractDeals = () => {
  const [contracts, setContracts] = useState([])
  const [contractsUser, setContractsUser] = useState([])
  const[showModalRenew, setShowModalRenew] = useState(false)



  useEffect(()=>{
    var test = JSON.parse(localStorage.getItem('testToken'))
    fetch("http://localhost:8081/api/contracts/getAllContracts",{
      headers : { 
        'Content-Type': 'application/json',
        'Accept': 'application/json',
        Authorization: `Bearer ${test.accessToken}`,
       },
    })
    .then(res =>res.json())
    .then((result)=>
    {
        setContracts(result);
    }
    )
  }, [])

  const navigate = useNavigate();
  
  const navigateToAddNew = (e) =>{
    e.preventDefault()
    window.location.href = "/CreateNewOrder"
}

const navigatetorequest = (e) =>{
    e.preventDefault()
    window.location.href = "/ContractRequests"
}
const sendContract = (e) =>{
    e.preventDefault()
    window.location.href = "/SendContract"
}

function handleCloseModalRenew(){
    setShowModalRenew(false);
}
function handleOpenModalRenew(){
    setShowModalRenew(true);
}

const rowEvent = {
    onClick: (e, row) => {
     
        navigate(`/SeeOrder/`+row.id);
    },
  }; 
  return(
   

    
        <body>
            <div class="topnav">
                <a href="/UserHomepage">Home Page</a>
                <a  href="/Orders">Your orders</a>
                <a class="active" href="/ContractDeals">Contracts</a>
                <a href="/UserProfileUpdate">Profile</a>
             
            </div>
           
            <div className='wrapper'>
                <table>
                    <tr>
                        <th>Contract id</th>
                        <th>Product</th>
                       
                        <th>Contract length</th>
                        <th>Price</th>
                        <th>Discount(%)</th>
                        <th>Quantity(pieces)</th>
                        <th>Total price</th>
                        <th>Delivery per week</th>
                        <th>Customer</th>

                    </tr>
                    {contracts.map((val, key) => {
                        return(
                            <tr key={key} >
                                <td>{val.id}</td>
                                <td>{val.productDTO.productName}</td>
                               
                                <td>{val.contractLength}</td>
                                <td>{val.productDTO.price}</td>
                                <td>{val.discount}</td>
                                <td>{val.quantity}</td>
                                <td>{val.totalPrice}</td>
                                <td>{val.perWeek}</td>
                                <td>{val.userDTO.username}</td>
                                <td>
                                <div>
                                        <button onClick={(e) => {
                                                    e.preventDefault()
                                                    
                                                    setShowModalRenew(true)
                                          
                                                    localStorage.setItem('contractId', val.id)
                                                    var contractId= localStorage.getItem('contractId')
                                                    var test = JSON.parse(localStorage.getItem('testToken'))
                                                    fetch("http://localhost:8081/api/contracts/" +contractId,{
                                                        headers : { 
                                                          'Content-Type': 'application/json',
                                                          'Accept': 'application/json',
                                                          Authorization: `Bearer ${test.accessToken}`,
                                                         },
                                                      })
                                                      .then(res =>res.json())
                                                      .then((result)=>
                                                      {
                                                          setContractsUser(result);
                                                      }
                                                      )
                                        
                                            }}>User info</button>
                                        <Modal id="modalRenew" isOpen={showModalRenew} onRequestClose={handleCloseModalRenew} style={{overlay: { backgroundColor: "rgba(0, 0, 0, 0.5)",},  content: {backgroundColor: "white",borderRadius: "5px",        padding: "20px",maxWidth: "500px", maxHeight: "700px", margin: "auto",},}}>
                                              <div className="wrapper">
      <form >
      <h1>Details about user</h1>
   
        <fieldset>
            <label>
                <p>First name</p>
                <input id="firstName" name="firstName" placeholder='Marko' />
            </label>
        </fieldset>



        <fieldset>
            <label>
                <p>Last name</p>
                <input id="lastName" name="lastName"  placeholder='Markovic' />
            </label>
        </fieldset>
        <fieldset>
            <label>
                <p>E mail</p>
                <input id="email" name="email"  placeholder='marko@gmail.com' />
            </label>
        </fieldset>
        <fieldset>
            <label>
                <p>Address</p>
                <input id="address" name="address"  placeholder='Markova ulica'/>
            </label>
        </fieldset>
        <fieldset>
            <label>
                <p>Company name</p>
                <input id="conLength" name="conLength" placeholder='FTN CODEDIGITAL' />
            </label>
        </fieldset>


        
        <button onClick={handleCloseModalRenew}>Close</button>
        <button onClick={sendContract}>Send contract</button>
        </form>
    </div>
                                           
                                        </Modal>
                                </div>
                                </td>
                                
                               
                            </tr>
                        )
                    })}
                </table>
                
            </div>
            <button class="request" onClick={navigatetorequest}>Contract requests</button>

        </body>
    
  )
 
}

export default ContractDeals
